mapper.select:
SELECT c.course_id, c.title, c.preview_image, u.nickname AS author, u.profile_image, (SELECT COUNT(*) FROM course_likes cl WHERE cl.course_id = c.course_id) AS likes FROM courses c JOIN users u ON c.author_id = u.id
c.type = 'COMMUNITY'
AND (c.title LIKE CONCAT('%', #{query}, '%') OR c.description LIKE CONCAT('%', #{query}, '%'))
AND c.area = #{area}
ORDER BY c.created_at DESC LIMIT #{offset}, #{itemsPerPage}

SELECT COUNT(*)
FROM course_likes
WHERE course_id = #{courseId} AND user_id = #{userId}

SELECT COUNT(*)
FROM course_likes
WHERE course_id = #{courseId}
