mapper.select:
SELECT * FROM user_preferences 
WHERE user_id = #{userId}
ORDER BY preference_score DESC

SELECT DISTINCT r.* FROM restaurants r JOIN reviews rev ON r.id = rev.restaurant_id WHERE rev.user_id IN
#{userId}
AND rev.rating >= 4 AND rev.is_active = true AND r.is_active = true AND r.id NOT IN ( SELECT restaurant_id FROM reviews WHERE user_id = #{currentUserId} AND is_active = true ) ORDER BY AVG(rev.rating) DESC, COUNT(rev.id) DESC LIMIT #{limit}

SELECT r.*
FROM restaurants r
WHERE r.is_active = true

    AND r.category = #{category}


    AND r.price_range = #{priceRange}


    AND r.atmosphere = #{atmosphere}

ORDER BY r.rating DESC, r.review_count DESC
LIMIT #{limit}

SELECT content_vector
FROM restaurant_vectors
WHERE restaurant_id = #{restaurantId}
