mapper.select:
SELECT * FROM restaurants
WHERE is_active = true AND kakao_place_id IS NULL

	AND category = #{category}


	AND location = #{location}


	AND name LIKE CONCAT('%', #{name}, '%')


	AND id IN (
	SELECT restaurant_id FROM reviews
	GROUP BY
	restaurant_id
	HAVING AVG(rating) >= #{minRating}
	)

ORDER BY created_at DESC

mapper.update:
UPDATE restaurants SET name = #{name}, category = #{category}, location = #{location}, address = #{address}, jibun_address = #{jibunAddress}, phone = #{phone}, description = #{description}, parking = #{parking}, latitude = #{latitude}, longitude = #{longitude}, operating_days = #{operatingDays}, operating_times_text = #{operatingTimesText}, break_time_text = #{breakTimeText},
image = #{image},
updated_at = NOW() WHERE id = #{id} AND owner_id = #{ownerId}

mapper.select:
SELECT id, name, address, location, phone, category, description, image, latitude, longitude, parking, created_at, updated_at, (6371 * ACOS( COS(RADIANS(#{latitude})) * COS(RADIANS(latitude)) * COS(RADIANS(longitude) - RADIANS(#{longitude})) + SIN(RADIANS(#{latitude})) * SIN(RADIANS(latitude)) )) AS distance FROM restaurants WHERE latitude IS NOT NULL AND longitude IS NOT NULL
AND category IN
#{item}
AND id NOT IN
#{id}
HAVING distance <= #{radiusKm} ORDER BY distance ASC LIMIT #{limit} OFFSET #{offset}

mapper.delete:
DELETE FROM restaurant_images
WHERE restaurant_id = #{restaurantId}
AND
image_path IN

	#{imageName}
